package interview_Programs;

public class traingle {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=5;i++) {
			
			for(int j=1;j<=i;j++) {
				int k=i*j;
				System.out.print(k);
				
			}
			System.out.println("");
		}

	}

}
